query myTeam($input: PaginatedInputTeamSearchValues!, $myTicketInput: PaginatedInputMyTicketsSearchValues!) {
  me {
    id
    email
    name
    lastName
    username
    bio
    pronouns
    imageUrl
    __typename
  }
  searchTeams(input: $input) {
    data {
      ...Team
      __typename
    }
    __typename
  }
  myTickets(input: $myTicketInput) {
    data {
      id
      approvalStatus
      createdAt
      redemptionStatus
      ticketTemplate {
        id
        name
        description
        startDateTime
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment Team on TeamRef {
  id
  description
  name
  status
  users {
    id
    role
    status
    user {
      id
      email
      name
      lastName
      username
      bio
      pronouns
      imageUrl
      __typename
    }
    __typename
  }
  event {
    id
    name
    startDateTime
    endDateTime
    description
    __typename
  }
  __typename
}
